AND SUBSTR( c.cur_stat, 1, 1 ) = 'X' AND gin_dt BETWEEN to_date( ${searchStartDt} || '000000', 'yyyymmddhh24miss' ) AND to_date( #{searchEndDt} || '235959', 'yyyymmddhh24miss' )
AND TRIM(d.unno) <> TRIM(c.unno) AND gin_dt BETWEEN to_date( #{searchStartDt} || '000000', 'yyyymmddhh24miss' ) AND to_date( #{searchEndDt} || '235959', 'yyyymmddhh24miss' )
